import { Component } from '@angular/core';
import axios from 'axios';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'urlShortner';


  shortenUrl() {
    const encodedParams = new URLSearchParams();
    encodedParams.set('url', 'https://github.com/itsnitinr/vscode-portfolio/tree/main');

    const options = {
      method: 'POST',
      url: 'https://shorturl9.p.rapidapi.com/functions/api.php',
      headers: {
        'content-type': 'application/x-www-form-urlencoded',
        'X-RapidAPI-Key': 'ba73ef9b6fmsh673be1f20a56d5fp1e47b0jsn754ffe50d2b1',
        'X-RapidAPI-Host': 'shorturl9.p.rapidapi.com'
      },
      data: encodedParams,
    };

    axios.request(options)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }

  outputText = 'This is the output text';

  copyToClipboard() {
    const inputElement = document.createElement('input');
    inputElement.value = this.outputText;
    document.body.appendChild(inputElement);
    inputElement.select();
    document.execCommand('copy');
    document.body.removeChild(inputElement);
  }


}
